import { createStore } from "vuex";
export default createStore({
  state: {
    authenticated: false,
    token: null,
  },
  getters: {},
  mutations: {
    getUserInfos(state, info) {
      if (info.token) {
        localStorage.setItem('INFO')
        state.authenticated = true;
        state.token = info.token;
      }
    },
    persistStore
  },
  actions: {},
  modules: {},
});
