import { createStore } from "vuex";
export default createStore({
  state: {
    authenticated: false,
    token: null,
  },
  getters: {},
  mutations: {
    getUserInfos(state, info) {
      if (info.token) {
        state.authenticated = true;
        state.token = info.token;
      }
    },
  },
  actions: {},
  modules: {},
});
